    <button
      type="button"
      className="py-2 px-10 bg-lime-600 hover:bg-lime-700 text-white font-bold uppercase rounded-lg transition duration-300 ease-in-out w-full text-center"
      onClick={() => setAlumno(alumno)}
    >
      Editar
    </button>

    <button
      type="button"
      className="py-2 px-10 bg-red-600 text-white font-bold uppercase rounded-lg transition duration-300  hover:bg-red-700  border-red-600 ml-3 w-full text-center mt-2"
      onClick={handleEliminar}
    >
      Eliminar
    </button>
