import Alumno from "./Alumno"

const ListadoAlumnos = ({alumnos, setAlumno, eliminarAlumno}) => {
    return (
        <div className="mx-5 my-10 bg-white shadow-md px-8 py-16 rounded-xl border-2 border-purple-400 overflow-y-scroll uppercase">
        {alumnos && alumnos.length ? (
          <>
            <h2 className="font-bold text-4xl text-center text-purple-400 mb-8">Listado de Alumnos</h2>
            {alumnos.map((alumno) => (
              <Alumno
                key={alumno.id}
                alumno={alumno}
                setAlumno={setAlumno}
                eliminarAlumno={eliminarAlumno}
              />
            ))}
          </>
        ) : (
          <>
            <p className="font-bold text-4xl text-center mb-8">
              ¡Comienza agregando alumnos!{' '}
              <span className="text-purple-400 font-extrabold">Aquí aparecerán todos</span>
            </p>
          </>
        )}
      </div>


    )
}

export default ListadoAlumnos
