import { useState, useEffect } from 'react';
import Error from './Error'

const Formulario = ({ alumnos, setAlumnos, alumno, setAlumno }) => {
    const [nombre, setNombre] = useState('');
    const [propietario, setPropietario] = useState('');
   /////////////////////////////
   const [foto, setFoto] = useState('');
//////////////
    const [carrera, setCarrera] = useState('');


    const [error, setError] = useState(false)

    useEffect(() => {
        if( Object.keys(alumno).length > 0  ) {
            setNombre(alumno.nombre)
            setPropietario(alumno.propietario)

            setCarrera(alumno.carrera)

            setFoto(alumno.foto)
        }
    }, [alumno])




    const generarId = () => {
        const random = Math.random().toString(36).substr(2);
        const fecha = Date.now().toString(36)
        return random + fecha
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        // Validación del Formulario
        if( [ nombre,propietario,foto,carrera ].includes('') ) {
            console.log('Hay Al Menos un campo vacio')

            setError(true)
            return;
        } 

        setError(false)


          // Objeto de Alumno
  const objetoAlumno = {
    nombre, 
    propietario, 
    carrera,
    foto: foto ? URL.createObjectURL(foto) : null // Convertir el archivo en una URL y almacenarla en el objeto del alumno, o almacenar null si no hay archivo seleccionado
  }

        if(alumno.id) {
            // Editando el Registro
            objetoAlumno.id = alumno.id
            const alumnosActualizados = alumnos.map( alumnoState => alumnoState.id === alumno.id ? objetoAlumno : alumnoState )

            setAlumnos(alumnosActualizados)
            setAlumno({})

        } else {
            // Nuevo registro
            objetoAlumno.id = generarId();
            setAlumnos([...alumnos, objetoAlumno]);
        }

        // Reiniciar el form
        setNombre('')
        setPropietario('')

        setCarrera('')


        setFoto(null); // Reiniciar el estado de la foto
    }

    return (
        <div className="md:w-1/2 lg:w-2/5 mx-auto">

            <p className="text-lg mt-5 text-center mb-10">
                Añade Alumnos y {''}
                <span className="text-lime-400   font-bold ">Administralos</span>
            </p>

            <form 
                onSubmit={handleSubmit}
                className="bg-white shadow-md rounded-lg py-10 px-5 mb-10"
            >
                { error &&  <Error><p>Todos los campos son obligatorios</p></Error>}
                <div className="mb-5">
                    <label htmlFor="alumno" className="block text-gray-700 uppercase font-bold">
                        Nombre del Alumno
                    </label>
                    <input
                        id="alumno"
                        type="text"
                        placeholder="Nombre del Alumno"
                        className="border-2 w-full p-2 mt-2 placeholder-gray-400 rounded-md"
                        value={nombre}
                        onChange={ (e) => setNombre(e.target.value) }
                    />  
                </div>

                <div className="mb-5">
                    <label htmlFor="propietario" className="block text-gray-700 uppercase font-bold">
                        Matricula
                    </label>
                    <input
                        id="propietario"
                        type="text"
                        placeholder="Matricula"
                        className="border-2 w-full p-2 mt-2 placeholder-gray-400 rounded-md"
                        value={propietario}
                        onChange={ (e) => setPropietario(e.target.value) }
                    />  
                </div>

                <div className="mb-5">
                    <label htmlFor="sintomas" className="block text-gray-700 uppercase font-bold">
                        Carrera
                    </label>
                    <input 
                        id="sintomas"
                        type="text"
                        className="border-2 w-full p-2 mt-2 placeholder-gray-400 rounded-md"
                        placeholder="Carrera"
                        value={sintomas}
                        onChange={ (e) => setSintomas(e.target.value) }
                    />
                </div>



                <div className="mb-5">
                    <label htmlFor="foto" className="block text-gray-700 uppercase font-bold">
                        Foto
                    </label>
                    <input
                        id="foto"
                        type="file"
                        accept="image/*"
                        className="border-2 w-full p-2 mt-2 placeholder-gray-400 rounded-md"
                        onChange={(e) => setFoto(e.target.files[0])}
                    />
                </div>









                <input
                    type="submit"
                    className="bg-lime-600 w-full p-3 text-white uppercase font-bold hover:bg-lime-700 cursor-pointer transition-colors"
                    value={ alumno.id ? 'Editar Alumno' : 'Agregar Alumno' }
                />
            </form>
        </div>
    )
}

export default Formulario
