import { useState, useEffect } from 'react';

const Paciente = ({paciente, setPaciente, eliminarPaciente}) => {
  const { nombre, propietario, foto, sintomas, id } = paciente

  const handleEliminar = () => {
      const respuesta = ('Deseas eliminar este paciente?');

      if(respuesta) {
          eliminarPaciente(id)
      }
  }


  const [usuarios, setUsuarios] = useState([]);

  useEffect(() => {
    // Aquí puedes obtener los datos de usuario de tu base de datos, incluyendo la URL de la imagen.
  }, []);

  return (
      <div className="mx-5 my-10 bg-white shadow-md px-5 py-10 rounded-xl">
          <p className="font-bold mb-3 text-gray-700 uppercase">Nombre: {''}
              <span className="font-normal normal-case">{nombre}</span>
          </p>

          <p className="font-bold mb-3 text-gray-700 uppercase">Matricula: {''}
              <span className="font-normal normal-case">{propietario}</span>
          </p>



          <p className="font-bold mb-3 text-gray-700 uppercase">Carrera: {''}
              <span className="font-normal normal-case">{sintomas}</span>
          </p>
          <p className="font-bold mb-3 text-gray-700 uppercase">Carrera: {''}
              <span className="font-normal normal-case">{foto}</span>
          </p>



          <ul>
              {usuarios.map(usuario => (
                  <li key={usuario.id}>
                      <h2>{usuario.nombre}</h2>
                      <img src={usuario.fotoUrl} alt="Foto de perfil" />
                  </li>
              ))}
          </ul>













          <div className="flex justify-between mt-10">


              <button 
                  type="button"
                  className="py-2 px-10 bg-lime-600 hover:bg-lime-700 text-white font-bold uppercase rounded-lg"
                  onClick={() => setPaciente(paciente)}
              >Editar</button>

              <button 
                  type="button"
                  className="py-2 px-10 bg-red-600 hover:bg-red-700 text-white font-bold uppercase rounded-lg"
                  onClick={handleEliminar}
              >Eliminar</button>
          </div>
      </div>
  )
}

export default Paciente
