const Alumno = ({ alumno, setAlumno, eliminarAlumno }) => {
    const { nombre, matricula, foto, carrera, id } = alumno

    const handleEliminar = () => {
      const respuesta = ('¿Deseas eliminar este alumno?')

      if (respuesta) {
        eliminarAlumno(id)
      }
    }

    return (
      <div className="mx-5 my-10 bg-white shadow-md px-5 py-10 rounded-xl border-2 border-lime-600" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '1rem' }}>

    <img src={foto} alt={nombre} className="w-40 h-40 rounded-full mx-auto" />

    <p className="font-bold mb-3 text-gray-700 uppercase text-shadow-lg">Nombre del Alumno: {''}
      <span className="font-normal normal-case">{nombre}</span>
    </p>

    <p className="font-bold mb-3 text-gray-700 uppercase text-shadow-lg">Matricula: {''}
      <span className="font-normal normal-case">{matricula}</span>
    </p>

    <p className="font-bold mb-3 text-gray-700 uppercase text-shadow-lg">Carrera: {''}
      <span className="font-normal normal-case">{carrera}</span>
    </p>

    <button
      type="button"
      className="py-2 px-10 bg-red-600 text-white font-bold uppercase rounded-lg transition duration-300 ease-in-out hover:bg-red-700 border-2 border-red-600"
      onClick={handleEliminar}
    >
      Eliminar
    </button>

    <button
      type="button"
      className="py-2 px-10 bg-lime-600 hover:bg-lime-700 text-white font-bold uppercase rounded-lg transition duration-300 ease-in-out"
      onClick={() => setAlumno(alumno)}
    >
      Editar
    </button>
</div>



    )
  }

  export default Alumno
