import Alumno from "./Alumno"

const ListadoAlumnos = ({alumnos, setAlumno, eliminarAlumno}) => {
    return (
        <div className=" bg-gray-300 p-4 md:w-1/6 lg:w-3/4 md:h-screen mx-64 overflow-y-scroll uppercase ">

            {alumnos && alumnos.length ? (
                <>
                    <h2 className="font-black text-3xl text-center">Listado Alumnos</h2>


                    { alumnos.map( alumno => (
                        <Alumno 
                            key={alumno.id}
                            alumno={alumno}
                            setAlumno={setAlumno}
                            eliminarAlumno={eliminarAlumno}
                        />
                    ))}

                </>

            ) : (
                <>

                    <p className="font-black text-3xl text-center">
                        Comienza agregando alumnos {''}
                        <span className="font-black  ">y aparecer√°n en este lugar</span>
                    </p>
                </>
            )}
        </div>
    )
}

export default ListadoAlumnos
